            <div className="col-10 max-auto col-md-12 text-center text-uppercase mb-3">
              <h1 className="text-center text-slanted">Recipe List</h1>
              </div>
          </div>
          <div className="row">
          {recipes.map(recipe => {
             return(<Recipe key={recipe.recipe_id} recipe={recipe}/>);
          })}
          </div>
        </div>

      </React.Fragment>
    );
  }
}

export default RecipeList;
